import React from "react";

function Card(props){
    return(
        <>
        <div className='cards'>
          <div className='card'>
            <img src={props.imgSrc} alt='mypPic' className='card_img' style={{width: '400px'}}/>
            <div className='card_info'>
              <span className='card_category'> {props.s}</span>
              <h3 className='card_title'>{props.title} </h3>
              <a href={props.link} target={"_blank"} >
                <button> Watch Now </button>
              </a>
            </div>
          </div>
        </div>
        </>
    );
}
export default Card;
